mapper.select:
SELECT 

FROM sys_schedule_job a

WHERE a.id = #{id}

SELECT
FROM sys_schedule_job a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.job_name LIKE '%'||#{jobName}||'%' '%'+#{jobName}+'%' concat('%',#{jobName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sys_schedule_job a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_schedule_job SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
